sites.css:
html body {
	padding-top: 0;
}

#content #mainbar {
	width: 100%;
}

.top-bar,
#question-header .aside-cta,
#left-sidebar,
#sidebar {
	display: none;
}

/* Cleanup unneeded stuff */
#header,
#ghx-header,
#ghx-operations,
.ghx-detail-nav-menu,
.ghx-project-avatar {
	display: none;
}


/* Set detail sidebar to 50% */
.ghx-detail-view {
	width: 50% !important;
}


/* Remove menu in the detail sidebar */
#ghx-detail-contents {
	background: linear-gradient(90deg, #ffe3f9 15px, #fff 0);
}
#ghx-detail-head,
#js-detail-nav-content {
	padding-left: 20px;
}
.ui-resizable-outer {
	display: none;
}

