/* ==================================================
 * BUNDLE ITEMS IN INBOX
 */

html div[gh="tl"] tr[labels*="Promotions"] ~ tr[labels*="Promotions"],
html div[gh="tl"] tr[labels*="Social"] ~ tr[labels*="Social"],
html div[gh="tl"] tr[labels*="Updates"] ~ tr[labels*="Updates"],
html div[gh="tl"] tr[labels*="Forums"] ~ tr[labels*="Forums"] {
	display: none;
}


/* ==================================================
 * SEARCH REFINEMENT BAR
 */

/* Move search refinement bar below action bar */
html .aeH div[gh="tm"] div[role="toolbar"] {
	position: absolute;
	top: 44px;
}

/* Hide search refinement bar when you scroll */
html .aeI .aeH div[gh="tm"] div[role="toolbar"] {
	display: none;
}

/* Move action bar into correct spot */
html .aeH div[gh="tm"] div[role="toolbar"] + .aqK {
	margin-top: 22px;
}

/* Hide Advanced search link */
html .aeH div[gh="tm"] div[role="toolbar"] div[data-impression-suffix="ADVANCED_SEARCH_LINK"],
html .aeH div[gh="tm"] div[role="toolbar"] div:first-child > div[role="button"]:last-child {
	display: none;
}

/* TODO: move search refinments to line up with the content under 1280px 
@media only screen and (max-width: 1278.98px) {
}
*/




/* ==================================================
 * INBOX DATE GROUPING
 */

tr[date="today"],
tr[date="yesterday"],
tr[date="month0"],
tr[date="month1"],
tr[date="month2"],
tr[date="earlier"] {
	margin-top: 3em;
}
tr[date="today"] ~ 		tr[date="today"],
tr[date="yesterday"] ~ 	tr[date="yesterday"],
tr[date="month0"] ~ 	tr[date="month0"],
tr[date="month1"] ~ 	tr[date="month1"],
tr[date="month2"] ~ 	tr[date="month2"],
tr[date="earlier"] ~ 	tr[date="earlier"] {
	margin-top: initial;
}

tr[date="today"]::before,
tr[date="yesterday"]::before,
tr[date="month0"]::before,
tr[date="month1"]::before,
tr[date="month2"]::before,
tr[date="earlier"]::before {
	height: 0px;
	width: 0px;
	white-space: nowrap;
	overflow: visible;
	font-size: 0.70em;
	letter-spacing: 1.2;
	color: #aaa;
	font-weight: bold;
	display: block;
	position: relative;
	top: -32px;
	left: 16px;
}

html.mediumTheme tr[date="today"]::before,
html.mediumTheme tr[date="yesterday"]::before,
html.mediumTheme tr[date="month0"]::before,
html.mediumTheme tr[date="month1"]::before,
html.mediumTheme tr[date="month2"]::before,
html.mediumTheme tr[date="earlier"]::before,
html.darkTheme tr[date="today"]::before,
html.darkTheme tr[date="yesterday"]::before,
html.darkTheme tr[date="month0"]::before,
html.darkTheme tr[date="month1"]::before,
html.darkTheme tr[date="month2"]::before,
html.darkTheme tr[date="earlier"]::before {
	color: #ffffff;
}

tr[date="today"]::before { content: 'TODAY'; }
tr[date="yesterday"]::before { content: 'YESTERDAY'; }
tr[date="month0"]::before { content: 'THIS MONTH'; }
tr[date="month1"]::before { content: 'LAST MONTH'; }
tr[date="month2"]::before { content: 'AUGUST'; }
tr[date="earlier"]::before { content: 'EARLIER'; }

tr[date="today"] ~ tr[date="today"]::before,
tr[date="yesterday"] ~ tr[date="yesterday"]::before,
tr[date="month0"] ~ tr[date="month0"]::before,
tr[date="month1"] ~ tr[date="month1"]::before,
tr[date="month2"] ~ tr[date="month2"]::before,
tr[date="earlier"] ~ tr[date="earlier"]::before {
	content: '';
	display: none;
}




/* ==================================================
 * GLOBAL / APP-LEVEL
 */

/* Hide footer */
html .aeG {
	display: none;
}

/* Move and restyle banners (vacation auto-responder, account delegation) */
html .w-MH,
html .ZY,
html .w-asV .aG,
html .bir {
	position: fixed;
	width: auto;
	max-width: 300px;
	text-align: left;
    padding: 18px 24px;
    bottom: 24px;
    left: 24px;
    font-size: .875rem;
	font-weight: bold;
	border: none;
	box-shadow: 0 1px 3px 0 rgba(60,64,67,0.302), 0 4px 8px 3px rgba(60,64,67,0.149);
	border-radius: 2px;

	/* Override the black on yellow default theme */
	color: #fff;
	background-color: #202124;
}
html .w-MH .e,
html .ZY .e,
html .w-asV .aG a,
html .bir a {
	margin-left: 16px;
	color: #8ab4f8;
}




/* ==================================================
 * APP BAR (menu and account switcher + container for search)
 */

/* Hide Gmail logo and name */
html #gb a[href="#inbox"] {
	display: none !important;
}

/* Hide App Switcher */
html #gbwa {
	display: none !important;
	position: absolute;
    right: 0px;
    padding: 12px 66px 14px 12px;
	margin: -8px -12px 0 0;
}
html.appSwitcher #gbwa {
	display: block !important;
}

/* Remove padding around profile photo so settings is better spaced */
/* UNSTABLE */
html .gb_Da {
	padding-left: 2px !important;
}

/* Clean up the butter bar (loading, etc) */
html .vX .vh {
	border: none;
    border-radius: 0 0 4px 4px;
    padding: 8px 16px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.06);
}

/* Reduce width of app bar when Addons are open */
html.addOnsOpen header#gb {
	width: calc(100vw - 300px);
}




/* ==================================================
 * ACTION BAR (actions, pagination, and settings)
 */

/* Move action bar on top of app bar and to right of menu button */
html .aeH {
	position: absolute !important;
	top: 0px !important;
	left: 60px !important;
	width: calc(var(--action-bar-width) - 92px);

	/* Reduce z-index from 986 so action bar can live on top of it */
	z-index: 6;
}
html .aeH .G-atb {
	height: 64px;
}

/* Remove extra right margin from action bar so it is spaced correctly */
html .aeH .G-atb[gh="tm"] {
	margin-right: 0px !important;
	top: 0px;
}

/* Hide extra drop shadow on action bar - it bleeds through on the filter pop-up */
html .aeH .G-atb::before {
	box-shadow: none;
	-webkit-box-shadow: none;
}

/* Make bar wider when search is hidden */
html.hideSearch .aeH {
	width: calc(100vw - 172px);
}
html.hideSearch.addOnsOpen .aeH {
	width: calc(100vw - 172px - 300px);
}

/* Dim pagination controls unless hovered over 
html .aeH .G-atb .aqJ,
html .aeF .G-atb .aqJ,
html .aeH .G-atb .adF {
	opacity: 0.5;
	transition: opacity 0.2s ease-in 0s;
}
html .aeH .G-atb .aqJ:hover,
html .aeF .G-atb .aqJ:hover,
html .aeH .G-atb .adF:hover {
	opacity: 1;
}
*/
/* Don't show pagination menu */
html .aeH .G-atb .aqJ .amG,
html .aeF .G-atb .aqJ .amG,
html .aeH .G-atb .adF .amG {
	display: none;
}

/* Below 1350px, hide pagination when search and addOns are both open */
@media only screen and (max-width: 1370.98px) {
	html.addOnsOpen:not(.hideSearch) .aeH .G-atb .aqJ > div:not([gh="s"]),
	html.addOnsOpen:not(.hideSearch) .aeH .G-atb .adF > div:not([gh="s"]) {
		display: none;
	}
}

/* Below 1200px, hide pagination AND settings when search and addOns are both open */
@media only screen and (max-width: 1200.98px) {
	html.addOnsOpen:not(.hideSearch) .adF,
	html.addOnsOpen:not(.hideSearch) .aqJ {
		display: none;
	}
}

/* Below 1100px wide, when search is expanded, hide pagination controls even if AddOns aren't open */
@media only screen and (max-width: 1100.98px) {
	html:not(.hideSearch) .aeH .G-atb .aqJ > div:not([gh="s"]),
	html:not(.hideSearch) .aeH .G-atb .adF > div:not([gh="s"]) {
		display: none;
	}
}

/* Below 850px wide, when search is expanded, pagination AND settings */
@media only screen and (max-width: 850.98px) {
	html:not(.hideSearch) .aeH .G-atb .aqJ,
	html:not(.hideSearch) .aeH .G-atb .adF {
		display: none;
	}
}

/* Remove drop shadow from action bar */
html .aeI .G-atb {
	box-shadow: none;
	-webkit-box-shadow: none;
}

/* Move action bar for Horizontal Multiple Inboxes */
html.multiBoxHorz .aeF .G-atb[gh="tm"] {
	position: fixed;
	top: 8px;
	left: 60px;
	z-index: 6 !important;
	width: calc(var(--action-bar-width) - 92px);
	transition: width 0s ease-in 0s; /* was 0.15s - Looks bad with 3rd party extensions */
}
html.multiBoxHorz .aeF .G-atb[gh="tm"]::before {
	box-shadow: none;
	-webkit-box-shadow: none;
}
html.multiBoxHorz.hideSearch .aeF .G-atb[gh="tm"] {
	width: calc(100vw - 172px) !important;
}

/* Further adjustments for side-by-side multiple inboxes */
html.multiBoxHorz div[role="main"] .byR {
	margin-top: 0px;
}

@media only screen and (max-width: 1441px) {
	html.multiBoxHorz .aeF > div.nH {
		max-width: 1400px !important;
	}
	/* Keep the max-width of the conversation narrow -- fragile due to use of .BltHke */
	/* TODO: I should do this at all resolutions for horizontal split view */
	html.multiBoxHorz .aeF > div.nH > div.nH:last-child:not(.BltHke) {
		max-width: 960px !important;
		margin: 0 auto;
	}
}
@media only screen and (min-width: 1441.98px) {
	/* Allow the side-by-side multiBox to be a lot wider... */
	html.multiBoxHorz .aeF > div.nH {
		max-width: 2200px !important;
	}
	/* ... while keeping the max-width of the conversation narrow */
	html.multiBoxHorz .aeF > div.nH > div.nH[role="main"]:not(.BltHke) {
		max-width: 1100px !important;
		margin: 0 auto;
	}
}

/* Nudge the offline button closer to the settings gear */
html .aqJ .bvX,
html .adF .bvX {
	margin-right: 16px !important;
}

/* Hide the Offline icon */
html .bvE {
	display: none;
}




/* ==================================================
 * ALIGN ACTION BAR WITH CONTENT
 */

/*
Resolutions
	Low (< 960px)
	Mid (961 - 1440)
	High (> 1441)

Views
	Default inbox w/ tabs
	Search (including label)
	Priority inbox
	Preview pane vertical
	Preview pane horizontal
	Multiple inboxes vertical
	Multiple inboxes horizontal

States (icon buttons)
	Menu closed
	Menu closed, Right side chat
	Menu closed, Right side chat, Search open
	Menu closed, Add-ons open
	Menu closed, Add-ons open, Search open
	Menu open
	Menu open, Right side chat
	Menu open, Right side chat, Search open
	Menu open, Add-ons open
	Menu open, Add-ons open, Search open

States (text button labels)
	Menu closed
	Menu closed, Right side chat
	Menu closed, Right side chat, Search open
	Menu closed, Add-ons open
	Menu closed, Add-ons open, Search open
	Menu open
	Menu open, Right side chat
	Menu open, Right side chat, Search open
	Menu open, Add-ons open
	Menu open, Add-ons open, Search open
 */

@media only screen and (min-width: 1278.98px) {
	/* DETAULT VIEWS: Move actions to left edge of content */
	html {
		--content-width: 960px;
		--left-offset: 60px;
	}
	html.navOpen {
		--left-offset: -32px;
	}
	html.rhsChat {
		--content-width: 1164px;
	}
	html.addOnsOpen,
	html.addOnsOpen.rhsChat {
		--content-width: 1260px;
	}
	html.addOnsOpen.rhsChat {
		--left-offset: 12px;
	}
	html.addOnsOpen.navOpen {
		--left-offset: -32px;
	}
	html.addOnsOpen.rhsChat.navOpen {
		--left-offset: -82px;
	}
	/* Apply variables to align left edge except if the menu and addOns or rhsChat are both open */
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.addOnsOpen):not(.rhsChat):not(.textButtons) .aeH .G-atb,
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.navOpen):not(.textButtons) .aeH .G-atb {
		padding-left: calc(((100vw - var(--content-width)) / 2) - var(--left-offset));
		margin-left: 16px !important;
	}

	/* Move pagination controls to right edge of content
	 * while leaving settings to far right */
	html.hideSearch {
		--right-offset: 158px;
	}
	html.hideSearch.navOpen {
		--right-offset: 252px;
	}
	html.hideSearch.rhsChat {
		--right-offset: -42px;
	}
	html.hideSearch.addOnsOpen.rhsChat {
		--right-offset: 208px;
	}
	html.hideSearch.navOpen.addOnsOpen,
	html.hideSearch.navOpen.addOnsOpen.rhsChat {
		--right-offset: 300px;
	}
	html.hideSearch.navOpen.rhsChat {
		--right-offset: 44px;
	}
	html.hideSearch.addOnsOpen.navOpen {
		--right-offset: 246px;
	}

	/* Apply variables to align right edge with content */
	html.hideSearch:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.addOnsOpen):not(.rhsChat):not(.textButtons) .aeH .ar5,
	html.hideSearch:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.addOnsOpen):not(.rhsChat):not(.textButtons) .adF .iG,
	html.hideSearch:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.navOpen):not(.textButtons) .aeH .ar5,
	html.hideSearch:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.navOpen):not(.textButtons) .adF .iG {
		padding-right: calc(((100vw - var(--content-width)) / 2) - var(--right-offset));
	}

	/* Keep pagination next to settings when search is open */
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.addOnsOpen):not(.rhsChat):not(.textButtons) .aeH .ar5,
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.addOnsOpen):not(.rhsChat):not(.textButtons) .adF .iG,
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.navOpen):not(.textButtons) .aeH .ar5,
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert):not(.navOpen):not(.textButtons) .adF .iG {
		padding-right: initial;
	}
}
@media only screen and (min-width: 1441.98px) {
	html {
		--content-width: 1100px;
	}
	html.rhsChat {
		--content-width: 1304px;
	}
	html.addOnsOpen,
	html.addOnsOpen.rhsChat {
		--content-width: 1400px;
	}

	/* Apply variables to align left edge for vertical preview pane when the nav is open */
	html.navOpen.splitView:not(.hideSearch):not(.multiBoxHorz):not(.multiBoxVert):not(.textButtons):not(.rhsChat):not(.addOnsOpen) .aeF .G-atb {
		padding-left: 196px;
		margin-left: 16px !important;
		width: calc(var(--action-bar-width) - 322px) !important;
	}
}
@media only screen and (min-width: 1638.98px) {
	/* Apply variables to align left edge */
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert) .aeH .G-atb {
		padding-left: calc(((100vw - var(--content-width)) / 2) - var(--left-offset));
		margin-left: 16px !important;
	}

	/* Apply variables to align right edge with content */
	html.hideSearch:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert) .aeH .ar5,
	html.hideSearch:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert) .adF .iG {
		padding-right: calc(((100vw - var(--content-width)) / 2) - var(--right-offset));
	}

	/* Keep pagination next to settings when search is open */
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert) .aeH .ar5,
	html:not(.splitView):not(.multiBoxHorz):not(.multiBoxVert) .adF .iG {
		padding-right: initial;
	}
}




/* ==================================================
 * SEARCH
 */

/* Push search to the right */
html #gb form {
	float: right;
	width: 20%;
	max-width: none;
	margin-right: 8px;
	position: relative;
	right: 0px;
	transition: width 0s;
}
html {
	--action-bar-width: 79%;
}

/* Make search wider and hide pagination/settings when search is focused */
html.searchFocused #gb form {
	width: 40%;
}
html.searchFocused {
	--action-bar-width: 59%;
}
html.searchFocused .aeH .G-atb .aqJ,
html.searchFocused .aeF .G-atb .aqJ,
html.searchFocused .aeH .G-atb .adF {
	display: none;
}

/* Shorter search box when using text label buttons 
html.textButtons #gb form {
	width: 25%;
}
html.textButtons {
	--action-bar-width: 74.5%;
}
*/

/* Shorter search box when nav is open on smaller screens 
@media only screen and (max-width: 1441.98px) {
	html.navOpen #gb form {
		width: 25%;
	}
	html.navOpen {
		--action-bar-width: 74.5%;
	}
}
*/

/* Minimize search to an icon when hidden */
html.hideSearch #gb form {
	width: 54px;
	margin-right: 0px;
	box-shadow: none;
	background-color: transparent;
}

/* Hide all search form buttons except search button when search is hidden */
html.hideSearch #gb form button {
	display: none;
}
html.hideSearch #gb form button[role="button"]:not([gh="sda"]) {
	display: block;
}

/* Move filter form to be under search box */
html .ZF-Av {
	left: auto !important;
	right: 71px !important;
	width: calc(100% - var(--action-bar-width) + 1%) !important;
	min-width: 450px;
}
html .ZF-Av > div {
	border: 0px;
}

/* Hide  or shrink search when app switcher is shown */
html.appSwitcher.hideSearch #gb form {
	display: none;
}
html.appSwitcher:not(.hideSearch) #gb form {
	width: calc(100% - var(--action-bar-width) - 1% - 50px);
	margin-right: 58px;
}

/* Hide search options button until you hover over search box 
html:not(.hideSearch) #gb form button[gh="sda"] {
	display: none;
}
html:not(.hideSearch) #gb form:hover button[gh="sda"] {
	display: block;
}
*/

/* Hide "Search the web" option from search auto-complete */
html #gb form td.gssb_e tr[role="separator"],
html #gb form td.gssb_e tr[role="separator"] + tr[role="option"] {
	display: none;
}




/* ==================================================
 * NAV & COMPOSE BUTTON
 */

/* SHOW NAV ON HOVER */
/* This is buggy b/c the animation is not instant and flipping between positions is */
html .bhZ:not(.bym) div[role="navigation"] {
	/* display: none; */
}
html .bhZ.bym {
	/* position: absolute; */
}

/* Don't show the nav when hovering over the compose button 
 * TODO: fix this, probably by adding a class on hover of the compose button
 */
html .bym {
	box-shadow: none !important;
}
html .bhZ div[role="complementary"],
html .bhZ div[gh="ns"] {
	display: none !important;
}

/* Hide nav when minimized or in Settings */
html .bhZ,
html.inSettings .aeN {
	position: relative;
	z-index: 3 !important;

	/* Remove this to show nav on hover */
	max-width: 0px !important;
	min-width: 0px !important;
}
html.inSettings .aeN {
	width: 0px !important;
	overflow: hidden;
}

/* No shadow or label on compose button */
html .z0 > .L3 {
	box-shadow: 0 1px 2px 0 rgba(60,64,67,0.3) !important;
	border-radius: 28px;
    font-size: 0;
    height: 56px;
    padding: 0;
    width: 56px;
}

/* Make FAB dark in dark themes */
html.darkTheme .z0 > .L3 {
	background-color: rgba(51,51,51,.9);
}

/* Move compose button out of menu */
html .z0 {
    position: absolute;
    left: calc(100vw - 92px);
    top: calc(100vh - 140px);
    z-index: 3 !important;
	width: 54px;
    margin: 0;
    padding: 0;
}

/* Move it over futher if RHS Chat is enabled */
html.rhsChat .z0 {
	left: calc(100vw - 275px);
}

/* Add some padding above the nav that the compose button used to provide */
html .wT {
	padding-top: 20px;
}

/* Hide top shadow when you scroll long label list */
html .aeN .ajj:before {
    -webkit-box-shadow: none;
    box-shadow: none;
}

/* Hide the compose and add-ons button when in Settings */
html.inSettings .z0,
html.inSettings .bAw {
	display: none;
}

/* Make the bottom Hangouts tabs a little less ugly */
html div[gh="gt"] {
	text-align: left;
	border-top: 0px;
	padding-left: 18px;
}
html.rhsChat div[gh="gt"] {
	text-align: right;
	border-top: 0px;
	padding-left: 0px;
}
html.rhsChat .akj {
	margin-top: 12px;
}

/* Remove extra right padding from rhsChat */
html.rhsChat .aCl .akj {
	padding-right: 0px !important;
}

/* Hide unread counts in nav if Simplify Setting enabled */
html.hideUnreads .bsU {
	display: none !important;
}



/* ==================================================
 * INBOX
 */

/* Bump up the line height a little in the inbox */
html.lowDensityInbox:not(.splitView) .zA {
    padding-top: 12px !important;
    padding-bottom: 12px !important;
}
html.highDensityInbox:not(.splitView) .zA {
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

/* Right and bottom padding on inbox and messages */
html .aeF {
	padding-right: 56px !important;
	margin-bottom: 150px;
}

/* Max width inbox and conversation and add top padding */
html .AO .aeF > .nH {
	margin: 40px auto 0 auto;
	max-width: 960px;
}
html.splitView.inSettings .AO .aeF > .nH {
	margin: 40px auto 100px auto !important;
	max-width: 960px !important;
}
@media only screen and (min-width: 1441.98px) {
	html:not(.splitView) .AO .aeF > .nH {
		max-width: 1100px;
	}
	html.splitView.inSettings .AO .aeF > .nH {
		max-width: 1100px !important;	
	}
}

/* Less padding on inbox and messages for low resolutions */
