	border-radius: 3px;
	border: 1px solid #b4b4b4;
	box-shadow: 0 1px 1px rgba(0, 0, 0, .2), 0 2px 0 0 rgba(255, 255, 255, .7) inset;
	color: #333;
	display: inline-block;
	font-size: 1em;
	font-family: monospace;
	font-weight: 700;
	line-height: 1;
	padding: 4px 6px;
	margin: 0 4px;
	white-space: nowrap;
}
#simplOptions .footer {
	opacity: 0.3;
}

#simplOptions table {
	padding: 0;
	margin: 0 auto;
	border: 0;
	border-spacing: 0;
	min-width: 550px;
}
#simplOptions td {
	padding: 16px 8px;
	text-align: left;
	border: 0;
}
#simplOptions tr td {
	border-bottom: 1px solid #ddd;
}
#simplOptions tr.label td {
	font-weight: bold;
}
#simplOptions tr:first-child td {
	border-top: 0px;
}
#simplOptions tr:not(.label):hover td {
	background-color: rgba(0,0,0,0.04);
}
#simplOptions td.toggle {
	width: 48px;
	text-align: center;
}

#simplOptions label {
	width: 100%;
	display: block;
}
#simplOptions label,
#simplOptions input {
	cursor: pointer;
}

#simplOptions .beta {
	display: none;
}

/* Toggle buttons */
#simplOptions button.toggle { 
	margin: 8px 0 0 0;
	border-radius: 20px;
	border: 2px solid #D0D0D0;
	height: 28px;
	cursor: pointer;
	width: 54px;
	position: relative;
	display: inline-block;
	user-select: none;
	-webkit-user-select: none;
	-ms-user-select: none;
	-moz-user-select: none;
}
#simplOptions button.toggle:focus {
	outline: none;
}
#simplOptions button.toggle span {
	position: absolute;
	left: 0;
	top: 0;
	border-radius: 100%;
	width: 26px;
	height: 26px;
	background-color: white;
	float: left;
	margin: -3px 0 0 -3px;
	border: 2px solid #D0D0D0;
 	transition: left 0.3s;
}
#simplOptions button.toggle.on { 
	background-color: #59E20E;
	border: 2px solid #4ECD09;
}
#simplOptions button.toggle.on span {
	left: 26px;
	top: 0;
	margin: 0;
	border: none;
	width: 24px;
	height: 24px;
	box-shadow: 0 0 4px rgba(0,0,0,0.1);
}



